import { useEffect, useState } from "react";
import SearchBar from "./components/SearchBar/SearchBar";
import RecomList from "./components/Recommendation/RecomList";
import FreeAppList from "./components/FreeApp/FreeAppList";
function App() {
  const [data, setData] = useState([]);
  const [recommenedAppsData, setRecommenedAppsData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const itemsPerPage = 10;

  useEffect(() => {
    fetch("https://itunes.apple.com/hk/rss/topfreeapplications/limit=100/json")
      .then((response) => response.json())
      .then((data) => {
        const items = data.feed.entry;
        setData(items);
      });
  }, []);

  useEffect(() => {
    fetch(
      "https://itunes.apple.com/hk/rss/topgrossingapplications/limit=10/json"
    )
      .then((response) => response.json())
      .then((data) => {
        const items = data.feed.entry;
        setRecommenedAppsData(items);
      });
  }, []);

  const fetchData = (page) => {
    setIsLoading(true);

    // Calculate the start index for the current page
    const startIndex = (page - 1) * itemsPerPage;

    fetch(
      `https://itunes.apple.com/hk/rss/topfreeapplications/limit=${itemsPerPage}/start=${startIndex}/json`
    )
      .then((response) => response.json())
      .then((newData) => {
        setData((prevData) => [...prevData, ...newData.feed.entry]);
        setCurrentPage(page);
        setIsLoading(false);
      });
  };

  // key: data.category.attributes["im:id"]
  // img: data["im:image"][2].label
  // label: data["im:name"].label
  // category: data.category.attributes.label

  // if (data.length > 0) {
  //   console.log(data[5]);
  //   console.log(data[7]);
  //   console.log(data[7].category.attributes.label);
  // }

  const extractedItems = data.map((item, index) => ({
    numbering: index + 1,
    key: item.id.attributes["im:id"],
    label: item["im:name"].label,
    icon: item["im:image"][2].label,
    category: item.category.attributes.label,
  }));

  const extractedRecItems = recommenedAppsData.map((item) => ({
    key: item.id.attributes["im:id"],
    label: item["im:name"].label,
    icon: item["im:image"][2].label,
    category: item.category.attributes.label,
  }));

  if (recommenedAppsData.length > 0) {
    console.log(recommenedAppsData[1]);
    console.log(recommenedAppsData[2]);
  }
  console.log(extractedItems[7]);
  // key : data.id.attributes["im:id"]
  // label: data.["im:name"].label
  // data.["im:image"][0].label
  // data.category.attributes.label

  // const app = [
  //   {
  //     key: 1,
  //     label: "dms",
  //     icon: "dfmsm",
  //     category: "dnfr",
  //     starRating: "dmlsmf",
  //   },
  // ];
  const handleScroll = () => {
    if (
      window.innerHeight + window.scrollY >= document.body.scrollHeight - 100 && // Add a threshold to trigger earlier
      !isLoading
    ) {
      const nextPage = currentPage + 1;
      fetchData(nextPage);
    }
  };
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [isLoading, currentPage]);

  return (
    <div>
      <SearchBar />
      <div style={{ marginLeft: "28px" }}>
        <h1 style={{ fontSize: "48px" }}>推介</h1>
        <RecomList item={extractedRecItems} />
      </div>
      <FreeAppList item={data} />
    </div>
  );
}

export default App;
