import { useEffect, useState } from "react";
import SearchBar from "./components/SearchBar/SearchBar";
import RecomList from "./components/Recommendation/RecomList";
import FreeAppList from "./components/FreeApp/FreeAppList";
function App() {
  const [data, setData] = useState([]);
  const [recommenedAppsData, setRecommenedAppsData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [displayedItems, setDisplayedItems] = useState([]);
  // const [isLoading, setIsLoading] = useState(true);
  const itemsPerPage = 10;

  useEffect(() => {
    fetch("https://itunes.apple.com/hk/rss/topfreeapplications/limit=100/json")
      .then((response) => response.json())
      .then((data) => {
        const items = data.feed.entry;
        setData(items);
      });

    fetch(
      "https://itunes.apple.com/hk/rss/topgrossingapplications/limit=10/json"
    )
      .then((response) => response.json())
      .then((data) => {
        const items = data.feed.entry;
        setRecommenedAppsData(items);
      });
  }, []);

  const extractedItems = data.map((item, index) => ({
    numbering: index + 1,
    key: item.id.attributes["im:id"],
    label: item["im:name"].label,
    icon: item["im:image"][2].label,
    category: item.category.attributes.label,
  }));

  const extractedRecItems = recommenedAppsData.map((item) => ({
    key: item.id.attributes["im:id"],
    label: item["im:name"].label,
    icon: item["im:image"][2].label,
    category: item.category.attributes.label,
  }));

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.scrollY >= document.body.offsetHeight &&
        currentPage * itemsPerPage < extractedItems.length
      ) {
        // Check if the current page is less than the maximum page number
        if (currentPage < Math.ceil(extractedItems.length / itemsPerPage)) {
          setCurrentPage((prevPage) => prevPage + 1);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [currentPage, extractedItems, itemsPerPage]);

  useEffect(() => {
    // Calculate the range of items to display
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    setDisplayedItems(extractedItems.slice(startIndex, endIndex));
  }, [currentPage, extractedItems, itemsPerPage]);

  return (
    <div>
      <SearchBar />
      <div style={{ marginLeft: "28px" }}>
        <h1 style={{ fontSize: "48px" }}>推介</h1>
        <RecomList item={extractedRecItems} />
      </div>
      <FreeAppList item={displayedItems} />
    </div>
  );
}

export default App;
