import "./FreeAppItem.css";
import styled from "styled-components";

const H2 = styled.h2`
  margin-right: 35px;
  color: #717171;
`;

const Container = styled.div`
  border-bottom: 1px solid #ababab;
  display: flex;
  align-items: center;
  padding: 18px 4px;
`;

// .container img,
// div {
//   margin-right: 15px;
// }

// .container *:last-child {
//   margin-right: 0;
// }

// .inner-div label,
// p,
// div {
//   margin: 0;
// }

// .inner-div p {
//   padding: 15px 0px;
//   color: #737373;
// }

// .icon {
//   width: 115px;
//   height: 115px;
// }

// .icon-rectangle {
//   border-radius: 30px;
// }

// .icon-circle {
//   border-radius: 60px;
// }

const FreeAppItem = (props) => {
  const isOdd = props.numbering % 2 === 1;
  const iconShape = isOdd ? "icon-rectangle" : "icon-circle";

  return (
    <Container>
      <H2 className="number">{props.numbering}</H2>
      <img className={`icon ${iconShape}`} src={props.icon} alt="app icon" />
      <div className="inner-div">
        <label style={{ fontSize: "25px" }}>{props.label}</label>
        <p>{props.category}</p>
        <div>{props.starRating}</div>
      </div>
    </Container>
  );
};

export default FreeAppItem;
