import { useEffect, useState } from "react";
import SearchBar from "./components/SearchBar/SearchBar";
import RecomItem from "./components/Recommendation/RecomItem";
import FreeAppList from "./components/FreeApp/FreeAppList";
function App() {
  const [data, setData] = useState([]);
  const apiURL =
    "https://itunes.apple.com/hk/rss/topfreeapplications/limit=100/json";

  useEffect(() => {
    fetch(apiURL)
      .then((response) => response.json())
      .then((data) => {
        const items = data.feed.entry;
        setData(items);
      });
  }, []);

  // if (data.length > 0) {
  //   console.log(data[5]);
  //   console.log(data[7]);
  //   console.log(data[7]["im:image"][0].label);
  // }

  const extractedItems = data.map((item) => ({
    key: item.id.attributes["im:id"],
    label: item["im:name"].label,
    icon: item["im:image"][0].label,
    category: item["im:image"][0].label,
  }));

  console.log(extractedItems[0]);
  // key : data.id.attributes["im:id"]
  // label: data.["im:name"].label
  // data.["im:image"][0].label
  // data.category.attributes.label

  const app = [
    {
      key: 1,
      label: "dms",
      icon: "dfmsm",
      category: "dnfr",
      starRating: "dmlsmf",
    },
  ];

  return (
    <div>
      <SearchBar />
      <h1>推介</h1>
      <RecomItem />
      <FreeAppList item={app} />
    </div>
  );
}

export default App;
