import { useState, useEffect } from "react";
import "./SearchBar.css";

const SearchBar = () => {
  const [input, setInput] = useState("");
  const [data, setData] = useState("");
  const [appResult, setAppResult] = useState([]);

  useEffect(() => {
    const fetchData = (userInput) => {
      if (userInput.trim() === "") {
        setData([]);
        return;
      } else {
        fetch(
          `https://itunes.apple.com/hk/search?term=${userInput}&entity=software`
        )
          .then((response) => response.json())
          .then((responseData) => {
            setData(responseData);

            const appItems = responseData.results.map((item) => ({
              name: item.trackName,
              author: item.artistName,
              category: item.primaryGenreName,
              description: item.description,
            }));
            setAppResult(appItems);
          });
      }
    };

    fetchData(input); // Call fetchData here with input as a dependency
  }, [input]); // Specify input as a dependency for the useEffect

  const submitHandler = (event) => {
    event.preventDefault();
  };

  const inputChangeHandler = (userInput) => {
    setInput(userInput);
  };

  return (
    <div className="search-bar">
      <form onSubmit={submitHandler}>
        <input
          value={input}
          onChange={(event) => inputChangeHandler(event.target.value)}
          type="text"
          placeholder="🔍  搜尋"
        />
      </form>
      <ul>
        {appResult.map((item, index) => (
          <li key={index}>
            <h3>{item.name}</h3>
            <p>Author: {item.author}</p>
            <p>Category: {item.category}</p>
            <p>Description: {item.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;

