import { useState, useEffect } from "react";
import "./SearchBar.css";
const SearchBar = () => {
  const [input, setInput] = useState("");
  const [data, setData] = useState("");
  const [appResult, setAppResult] = useState([]);

  const fetchData = (userInput) => {
    // if (userInput.trim() === "") {
    //   setAppResult([]);
    //   return;
    // } else {
    fetch(
      `https://itunes.apple.com/hk/search?term=${userInput}&entity=software`
    )
      .then((response) => response.json())
      .then((data) => setData(data));

    const appItems = data.results.map((item) => ({
      name: item.trackName,
      author: item.artisName,
      aategory: item.primaryGenreName,
      descriptoion: item.description,
    }));
  };
  // };

  const submitHandler = (event) => {
    event.preventDefault();
  };
  const inputChangeHandler = (userInput) => {
    setInput(userInput);
    fetchData(userInput);
  };

  return (
    <div className="search-bar">
      <form onSubmit={submitHandler}>
        <input
          value={input}
          onChange={(event) => inputChangeHandler(event.target.value)}
          type="text"
          placeholder="🔍  搜尋"
        />
      </form>
    </div>
  );
};

export default SearchBar;

