import { useState, useEffect } from "react";
import "./SearchBar.css";

const SearchBar = () => {
  const [input, setInput] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchSuggestions = (query) => {
    if (query.trim() === "") {
      setSuggestions([]);
      return;
    }

    setLoading(true);

    // Use the iTunes Search API to fetch suggestions
    fetch(`https://itunes.apple.com/hk/search?term=${query}&entity=software`)
      .then((response) => response.json())
      .then((data) => {
        if (data.results && data.results.length > 0) {
          // Extract app names from the API response
          const appNames = data.results.map((result) => result.trackName);
          setSuggestions(appNames);
        } else {
          setSuggestions([]);
        }
      })
      .catch((error) => {
        console.error(error);
        setSuggestions([]);
      })
      .finally(() => setLoading(false));
  };

  const inputChangeHandler = (value) => {
    setInput(value);
    fetchSuggestions(value);
  };

  return (
    <div className="search-bar">
      <input
        value={input}
        onChange={(event) => inputChangeHandler(event.target.value)}
        type="text"
        placeholder="🔍  搜尋"
      />
      {loading && <p>Loading suggestions...</p>}
      <ul className="suggestions">
        {suggestions.map((suggestion, index) => (
          <li key={index}>{suggestion}</li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
