import { useState, useEffect } from "react";
import "./SearchBar.css";
const SearchBar = () => {
  const [input, setInput] = useState("");
  const [data, setData] = useState("");
  const [appResult, setAppResult] = useState([]);

  const fetchData = (userInput) => {
    if (userInput.trim() === "") {
      setData([]);
      return;
    } else {
      fetch(
        `https://itunes.apple.com/hk/search?term=${userInput}&entity=software`
      )
        .then((response) => response.json())
        .then((responseData) => {
          setData(responseData);

          const appItems = responseData.results.map((item) => ({
            name: item.trackName,
            author: item.artistName,
            category: item.primaryGenreName,
            description: item.description,
          }));
          setData(appItems);
        });
    }
  };
  // console.log(data);

  // useEffect(() => {
  //   fetchData(input);
  // }, [input]);

  const dataArr = Object.values(data);
  console.log(dataArr);

  const submitHandler = (event) => {
    event.preventDefault();
  };
  const inputChangeHandler = (userInput) => {
    setInput(userInput);
    fetchData(userInput);
    setAppResult(
      dataArr.filter((f) => {
        const keyWord = userInput.toLowerCase();
        return (
          (f.name && f.name.toLowerCase().includes(keyWord)) ||
          (f.author && f.author.toLowerCase().includes(keyWord)) ||
          (f.category && f.category.toLowerCase().includes(keyWord)) ||
          (f.description && f.description.toLowerCase().includes(keyWord))
        );
      })
    );
  };

  return (
    <div className="search-bar">
      <form onSubmit={submitHandler}>
        <input
          value={input}
          onChange={(event) => inputChangeHandler(event.target.value)}
          type="text"
          placeholder="🔍  搜尋"
        />
      </form>
      <ul>{appResult}</ul>
    </div>
  );
};

export default SearchBar;

